import React from 'react';
import { useNavigate } from 'react-router-dom';
import styles from './Collections.module.css';

const styleCategories = [
  {
    id: 3,
    name: 'Polo Style',
    image: '/images/polo-style.jpg'
  },
  {
    id: 2,
    name: 'Casual',
    image: '/images/casual.jpg'
  },
  {
    id: 4,
    name: 'Sunny look',
    image: '/images/sunny-look.jpg'
  },
  {
    id: 1,
    name: 'Trendy',
    image: '/images/trendy.jpg'
  }
];

export default function Collections() {
  const navigate = useNavigate();

  const handleStyleClick = (styleId, styleName) => {
    navigate(`/style/${styleId}`);
  };

  return (
    <div className={styles.container}>
      <header className={styles.header}>
        <button 
          className={styles.backButton}
          onClick={() => navigate('/catalog')}
        >
          ‚Üê
        </button>
        <h1 className={styles.title}>Collections</h1>
        <div className={styles.headerIcons}>
          <button className={styles.iconButton}>üîç</button>
          <button className={styles.iconButton}>‚öôÔ∏è</button>
        </div>
      </header>

      <nav className={styles.navigation}>
        <button className={`${styles.navButton} ${styles.active}`}>
          All
        </button>
        <button className={styles.navButton}>
          Favorites
        </button>
      </nav>

      <p className={styles.startCustomize}>Start customizing</p>

      <div className={styles.grid}>
        {styleCategories.map((category) => (
          <div 
            key={category.id} 
            className={styles.card}
            onClick={() => handleStyleClick(category.id, category.name)}
          >
            <div className={styles.imageContainer}>
              <img 
                src={category.image} 
                alt={category.name}
                className={styles.image}
                onError={(e) => {
                  console.error(`Failed to load image: ${category.image}`);
                  e.target.src = '/images/placeholder.png';
                }}
              />
              <span className={styles.label}>{category.name}</span>
            </div>
          </div>
        ))}
      </div>

      <button className={styles.addButton}>+</button>
    </div>
  );
}
