import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import styles from './StyleEditor.module.css';

const toolbarItems = [
  { id: 1, icon: 'üì∑', label: 'Camera' },
  { id: 2, icon: 'üé®', label: 'Style' },
  { id: 3, icon: 'üìè', label: 'Grid' },
  { id: 4, icon: 'üìê', label: 'Layout' },
  { id: 5, icon: 'üí∞', label: 'Price' },
  { id: 6, icon: '‚ùå', label: 'Remove' }
];

const profileImages = [
  { id: 1, image: '/assets1/model1.png', label: 'Style 1' },
  { id: 2, image: '/assets1/model1.png', label: 'Style 2' },
  { id: 3, image: '/assets1/model1.png', label: 'Style 3' },
  { id: 4, image: '/assets1/model1.png', label: 'Style 4' },
  { id: 5, image: '/assets1/model1.png', label: 'Style 5' },
  { id: 6, image: '/assets1/model1.png', label: 'Style 6' },
  { id: 7, image: '/assets1/model1.png', label: 'Style 7' }
];

const categories = [
  'Basic jumpers',
  'Warm puffer',
  'Bomber',
  'Biker jacket'
];

const bottomTools = [
  { icon: 'üëî', label: 'Collar' },
  { icon: 'üëï', label: 'Styles' },
  { icon: 'üëñ', label: 'Pant' },
  { icon: 'üß•', label: 'Coat' },
  { icon: 'üëú', label: 'Accessories' },
  { icon: 'üîß', label: 'Tools' }
];

export default function StyleEditor() {
  const navigate = useNavigate();
  const [isFavorite, setIsFavorite] = useState(false);
  const [selectedProfile, setSelectedProfile] = useState(0);
  const [selectedCategory, setSelectedCategory] = useState(0);
  const [imageError, setImageError] = useState(false);
  const [isProcessing, setIsProcessing] = useState(false);

  const handleClose = () => {
    navigate(-1);
  };

  const toggleFavorite = () => {
    setIsFavorite(!isFavorite);
  };

  const handleSearch = () => {
    navigate('/clothing-catalog');
  };

  const handleProfileSelect = (index) => {
    setSelectedProfile(index);
    setImageError(false);
  };

  const handleImageError = () => {
    console.error('Failed to load image:', profileImages[selectedProfile].image);
    setImageError(true);
  };

  return (
    <div className={styles.container}>
      {/* Status Bar */}
      <div className={styles.statusBar}>
        <span>12:30</span>
        <div className={styles.statusIcons}>
          <span>üì∂</span>
          <span>üîã</span>
        </div>
      </div>

      {/* Header */}
      <div className={styles.header}>
        <button className={styles.closeButton} onClick={handleClose}>‚úï</button>
        <div className={styles.sparkle}>‚ú®</div>
        <div className={styles.headerRight}>
          <button className={styles.iconButton}>
            <img src="/assets1/images/cart.png" alt="Cart" />
          </button>
          <button className={styles.iconButton} onClick={toggleFavorite}>
            {isFavorite ? '‚ù§Ô∏è' : 'ü§ç'}
          </button>
        </div>
      </div>

      {/* Main Content */}
      <div className={styles.mainContent}>
        {/* Main Image */}
        <div className={styles.mainImageContainer}>
          <img 
            src="/assets1/model1.png"
            alt="Style preview"
            className={styles.mainImage}
            onError={(e) => {
              e.target.src = '/assets1/model1.png';
            }}
          />
          {isProcessing && (
            <div className={styles.processingOverlay}>
              <div className={styles.processingSpinner}>
                <div></div>
              </div>
            </div>
          )}
        </div>

        {/* Right Toolbar */}
        <div className={styles.rightToolbar}>
          <div className={styles.toolsLabel}>Tools</div>
          {toolbarItems.map((item) => (
            <button key={item.id} className={styles.toolButton}>
              {item.icon}
            </button>
          ))}
        </div>

        {/* Profile Circles */}
        <div className={styles.profilesContainer}>
          {profileImages.map((profile, index) => (
            <button
              key={profile.id}
              className={`${styles.profileButton} ${selectedProfile === index ? styles.selectedProfile : ''}`}
              onClick={() => handleProfileSelect(index)}
            >
              <img 
                src={profile.image} 
                alt={profile.label}
                className={styles.profileImage}
              />
            </button>
          ))}
        </div>

        {/* Categories */}
        <div className={styles.categoriesContainer}>
          {categories.map((category, index) => (
            <button
              key={index}
              className={`${styles.categoryButton} ${selectedCategory === index ? styles.selectedCategory : ''}`}
              onClick={() => setSelectedCategory(index)}
            >
              {category}
            </button>
          ))}
          <button className={styles.searchButton} onClick={handleSearch}>
            üîç
          </button>
        </div>

        {/* Bottom Navigation */}
        <div className={styles.bottomNav}>
          {bottomTools.map((tool, index) => (
            <button key={index} className={styles.navItem}>
              <span className={styles.navIcon}>{tool.icon}</span>
              <span className={styles.navLabel}>{tool.label}</span>
            </button>
          ))}
        </div>
      </div>
    </div>
  );
}
