import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import styles from './StyleEditor.module.css';

const toolbarItems = [
  { id: 1, icon: 'üì∑', label: 'Camera' },
  { id: 2, icon: 'üé®', label: 'Style' },
  { id: 3, icon: 'üìè', label: 'Grid' },
  { id: 4, icon: 'üì±', label: 'Layout' },
  { id: 5, icon: 'üí∞', label: 'Price' },
  { id: 6, icon: '‚ùå', label: 'Remove' }
];

export default function StyleEditor() {
  const navigate = useNavigate();
  const [isFavorite, setIsFavorite] = useState(false);

  const handleClose = () => {
    navigate(-1);
  };

  const toggleFavorite = () => {
    setIsFavorite(!isFavorite);
  };

  const handleToolClick = (tool) => {
    if (tool === 'Style') {
      navigate('/clothing-catalog');
    }
  };

  return (
    <div className={styles.container}>
      {/* Header */}
      <div className={styles.header}>
        <button className={styles.closeButton} onClick={handleClose}>
          ‚úï
        </button>
        <div className={styles.sparkle}>‚ú®</div>
        <div className={styles.headerRight}>
          <button className={styles.iconButton}>üõí</button>
          <button 
            className={styles.iconButton}
            onClick={toggleFavorite}
          >
            {isFavorite ? '‚ù§Ô∏è' : 'ü§ç'}
          </button>
        </div>
      </div>

      {/* Main Content */}
      <div className={styles.mainContent}>
        <div className={styles.imageContainer}>
          <img 
            src="/assets1/model1.png"
            alt="Style preview"
            className={styles.mainImage}
          />
        </div>

        {/* Right Toolbar */}
        <div className={styles.rightToolbar}>
          <div className={styles.toolsLabel}>Tools</div>
          {toolbarItems.map((item) => (
            <button 
              key={item.id} 
              className={styles.toolButton}
              onClick={() => handleToolClick(item.label)}
            >
              <span className={styles.toolIcon}>{item.icon}</span>
              <span className={styles.toolLabel}>{item.label}</span>
            </button>
          ))}
        </div>
      </div>
    </div>
  );
}
