import React, { useState } from 'react';
import styled from '@emotion/styled';
import SearchBar from './components/SearchBar';
import ClothingCard from './components/ClothingCard';

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 20px;
  background-color: #ffffff;
  min-height: 100vh;
`;

const Title = styled.h1`
  text-align: center;
  color: #333;
  margin-bottom: 30px;
  font-size: 2rem;
  font-weight: 600;
`;

const Grid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(200px, 1fr));
  gap: 25px;
  padding: 10px;
`;

// Default image for items that fail to load
const DEFAULT_IMAGE = 'data:image/svg+xml;base64,';

const clothingItems = [
  { id: 1, name: 'Coat', image: 'https://images.unsplash.com/photo-1539533113208-f6df8cc8b543?w=400&q=80' },
  { id: 2, name: 'Warm puffer', image: 'https://images.unsplash.com/photo-1547624643-3bf761b09502?w=400&q=80' },
  { id: 3, name: 'Bomber', image: 'https://images.unsplash.com/photo-1592878904946-b3cd8ae243d0?w=400&q=80' },
  { id: 4, name: 'Biker jacket', image: 'https://images.unsplash.com/photo-1551028719-00167b16eac5?w=400&q=80' },
  { id: 5, name: 'Denim jacket', image: 'https://images.unsplash.com/photo-1601333144130-8cbb312386b6?w=400&q=80' },
  { id: 6, name: 'Blazer (suit)', image: 'https://images.unsplash.com/photo-1594938298603-c8148c4dae35?w=400&q=80' },
  { id: 7, name: 'Printed/checked', image: 'https://images.unsplash.com/photo-1596755094514-f87e34085b2c?w=400&q=80' },
  { id: 8, name: 'Basic tees', image: 'https://images.unsplash.com/photo-1521572163474-6864f9cf17ab?w=400&q=80' },
  { id: 9, name: 'Baseball tee', image: 'https://images.unsplash.com/photo-1529374255404-311a2a4f1fd9?w=400&q=80' },
  { id: 10, name: 'Crew neck sweat', image: 'https://images.unsplash.com/photo-1578587018452-892bacefd3f2?w=400&q=80' },
];

function App() {
  const [searchQuery, setSearchQuery] = useState('');

  const filteredItems = clothingItems.filter(item =>
    item.name.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <Container>
      <Title>Clothing Catalog</Title>
      <SearchBar
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
      />
      <Grid>
        {filteredItems.map(item => (
          <ClothingCard
            key={item.id}
            name={item.name}
            image={item.image}
          />
        ))}
      </Grid>
    </Container>
  );
}

export default App;
