import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import styles from './StyleSelector.module.css';

const profileImages = [
  { id: 1, image: '/assets1/profiles/profile1.png' },
  { id: 2, image: '/assets1/profiles/profile2.png' },
  { id: 3, image: '/assets1/profiles/profile3.png' },
  { id: 4, image: '/assets1/profiles/profile4.png' },
  { id: 5, image: '/assets1/profiles/profile5.png' },
  { id: 6, image: '/assets1/profiles/profile6.png' },
  { id: 7, image: '/assets1/profiles/profile7.png' }
];

const categories = [
  'Basic jumpers',
  'Warm puffer',
  'Bomber',
  'Biker jacket'
];

const tools = [
  { icon: '👔', label: 'Collar' },
  { icon: '👕', label: 'Styles' },
  { icon: '👖', label: 'Pant' },
  { icon: '🧥', label: 'Coat' },
  { icon: '👜', label: 'Accessories' },
  { icon: '🔧', label: 'Tools' }
];

export default function StyleSelector() {
  const navigate = useNavigate();
  const [selectedProfile, setSelectedProfile] = useState(0);
  const [selectedCategory, setSelectedCategory] = useState(0);

  const handleClose = () => {
    navigate(-1);
  };

  const handleProfileClick = (index) => {
    setSelectedProfile(index);
    navigate('/ai-processing');
  };

  return (
    <div className={styles.container}>
      {/* Header */}
      <div className={styles.header}>
        <button className={styles.closeButton} onClick={handleClose}>
          ✕
        </button>
        <div className={styles.headerIcons}>
          <button className={styles.iconButton}>🛒</button>
          <button className={styles.iconButton}>🤍</button>
        </div>
      </div>

      {/* Main Image */}
      <div className={styles.mainImageContainer}>
        <img 
          src={profileImages[selectedProfile].image}
          alt="Style preview"
          className={styles.mainImage}
        />
      </div>

      {/* Profile Circles */}
      <div className={styles.profilesContainer}>
        {profileImages.map((profile, index) => (
          <button
            key={profile.id}
            className={`${styles.profileButton} ${selectedProfile === index ? styles.selectedProfile : ''}`}
            onClick={() => handleProfileClick(index)}
          >
            <img 
              src={profile.image} 
              alt={`Profile ${index + 1}`}
              className={styles.profileThumbnail}
            />
          </button>
        ))}
      </div>

      {/* Categories */}
      <div className={styles.categories}>
        {categories.map((category, index) => (
          <button
            key={index}
            className={`${styles.categoryButton} ${selectedCategory === index ? styles.selectedCategory : ''}`}
            onClick={() => setSelectedCategory(index)}
          >
            {category}
          </button>
        ))}
        <button className={styles.searchButton}>
          🔍
        </button>
      </div>

      {/* Bottom Toolbar */}
      <div className={styles.toolbar}>
        {tools.map((tool, index) => (
          <button 
            key={index}
            className={styles.toolButton}
          >
            <span className={styles.toolIcon}>{tool.icon}</span>
            <span className={styles.toolLabel}>{tool.label}</span>
          </button>
        ))}
      </div>
    </div>
  );
}
