import React, { useState } from 'react';
import styled from '@emotion/styled';
import SearchBar from './components/SearchBar';
import ClothingCard from './components/ClothingCard';

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 20px;
`;

const Title = styled.h1`
  text-align: center;
  color: #333;
  margin-bottom: 30px;
  font-size: 2rem;
`;

const Grid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(200px, 1fr));
  gap: 25px;
  padding: 10px;
`;

// Default image for items that fail to load
const DEFAULT_IMAGE = 'data:image/svg+xml;base64,';

const clothingItems = [
  { id: 1, name: 'Coat', image: DEFAULT_IMAGE },
  { id: 2, name: 'Warm puffer', image: DEFAULT_IMAGE },
  { id: 3, name: 'Bomber', image: DEFAULT_IMAGE },
  { id: 4, name: 'Biker jacket', image: DEFAULT_IMAGE },
  { id: 5, name: 'Denim jacket', image: DEFAULT_IMAGE },
  { id: 6, name: 'Blazer (suit)', image: DEFAULT_IMAGE },
  { id: 7, name: 'Printed/checked', image: DEFAULT_IMAGE },
  { id: 8, name: 'Basic tees', image: DEFAULT_IMAGE },
  { id: 9, name: 'Baseball tee', image: DEFAULT_IMAGE },
  { id: 10, name: 'Crew neck sweat', image: DEFAULT_IMAGE },
];

function App() {
  const [searchQuery, setSearchQuery] = useState('');

  const filteredItems = clothingItems.filter(item =>
    item.name.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <Container>
      <Title>Clothing Catalog</Title>
      <SearchBar
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
      />
      <Grid>
        {filteredItems.map(item => (
          <ClothingCard
            key={item.id}
            name={item.name}
            image={item.image}
          />
        ))}
      </Grid>
    </Container>
  );
}

export default App;
