import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import styles from './StyleDetails.module.css';

const styleVariations = [
  { id: 1, image: '/assets1/man.png' },
  { id: 2, image: '/assets1/Frame 1597881673.png' },
  { id: 3, image: '/assets1/Frame 1597881674.png' },
  { id: 4, image: '/assets1/Frame 1597881674 (1).png' },
  { id: 5, image: '/assets1/polo-style.png' },
  { id: 6, image: '/assets1/man.png' },
  { id: 7, image: '/assets1/Frame 1597881673.png' }
];

const categories = [
  'Basic jumpers',
  'Warm puffer',
  'Bomber',
  'Biker jacket'
];

const tools = [
  { icon: '👔', label: 'Collar' },
  { icon: '👕', label: 'Styles' },
  { icon: '👖', label: 'Pant' },
  { icon: '🧥', label: 'Coat' },
  { icon: '👜', label: 'Accessories' },
  { icon: '🔧', label: 'Tools' }
];

export default function StyleDetails() {
  const navigate = useNavigate();
  const [isFavorite, setIsFavorite] = useState(false);
  const [selectedVariation, setSelectedVariation] = useState(0);
  const [selectedCategory, setSelectedCategory] = useState(0);

  const handleClose = () => {
    navigate(-1);
  };

  const toggleFavorite = () => {
    setIsFavorite(!isFavorite);
  };

  const handleVariationClick = (index) => {
    setSelectedVariation(index);
    navigate('/ai-processing');
  };

  const handleToolClick = (toolLabel) => {
    if (toolLabel === 'Styles') {
      navigate('/style-selector');
    }
  };

  return (
    <div className={styles.container}>
      {/* Header */}
      <div className={styles.header}>
        <button className={styles.closeButton} onClick={handleClose}>
          ✕
        </button>
        <div className={styles.headerRight}>
          <button className={styles.iconButton}>🛒</button>
          <button 
            className={styles.iconButton}
            onClick={toggleFavorite}
          >
            {isFavorite ? '❤️' : '🤍'}
          </button>
        </div>
      </div>

      {/* Main Image */}
      <div className={styles.mainImageContainer}>
        <img 
          src={styleVariations[selectedVariation].image}
          alt="Style preview"
          className={styles.mainImage}
        />
      </div>

      {/* Style Variations */}
      <div className={styles.variationsContainer}>
        {styleVariations.map((variation, index) => (
          <button
            key={variation.id}
            className={`${styles.variationButton} ${selectedVariation === index ? styles.selectedVariation : ''}`}
            onClick={() => handleVariationClick(index)}
          >
            <img 
              src={variation.image} 
              alt={`Style ${index + 1}`}
              className={styles.variationThumbnail}
            />
          </button>
        ))}
      </div>

      {/* Categories */}
      <div className={styles.categories}>
        {categories.map((category, index) => (
          <button
            key={index}
            className={`${styles.categoryButton} ${selectedCategory === index ? styles.selectedCategory : ''}`}
            onClick={() => setSelectedCategory(index)}
          >
            {category}
          </button>
        ))}
        <button className={styles.searchButton}>
          🔍
        </button>
      </div>

      {/* Bottom Toolbar */}
      <div className={styles.toolbar}>
        {tools.map((tool, index) => (
          <button 
            key={index}
            className={styles.toolButton}
            onClick={() => handleToolClick(tool.label)}
          >
            <span className={styles.toolIcon}>{tool.icon}</span>
            <span className={styles.toolLabel}>{tool.label}</span>
          </button>
        ))}
      </div>
    </div>
  );
}
