import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import styles from './Collections.module.css';

const styleCategories = [
  {
    id: 1,
    name: 'Casual',
    image: '/assets1/Frame 1597881673.png'
  },
  {
    id: 2,
    name: 'Trendy',
    image: '/assets1/Frame 1597881674.png'
  },
  {
    id: 3,
    name: 'Polo Style',
    image: '/assets1/polo-style.png'
  },
  {
    id: 4,
    name: 'Sunny look',
    image: '/assets1/Frame 1597881674 (1).png'
  }
];

export default function Collections() {
  const navigate = useNavigate();
  const [totalClicks, setTotalClicks] = useState(0);

  const handleStyleClick = (styleId) => {
    setTotalClicks(prev => {
      const newCount = prev + 1;
      
      if (newCount === 3) {
        // On third click, show the Subscribe Now image
        const subscribeImage = require('../assets/images/Subscribe Now!.png');
        const img = new Image();
        img.src = subscribeImage;
        img.onload = () => {
          navigate('/subscribe');
        };
        return 0; // Reset counter
      } else {
        // First and second clicks go to style pages
        navigate(`/style/${styleId}`);
        return newCount;
      }
    });
  };

  const handleBackClick = () => {
    navigate('/catalog');
  };

  return (
    <div className={styles.container}>
      <div className={styles.header}>
        <button className={styles.backButton} onClick={handleBackClick}>←</button>
        <h1>Collections</h1>
        <button className={styles.menuButton}>☰</button>
      </div>

      <div className={styles.grid}>
        {styleCategories.map((category) => (
          <div 
            key={category.id} 
            className={styles.card}
            onClick={() => handleStyleClick(category.id)}
          >
            <div className={styles.imageContainer}>
              <img
                src={category.image}
                alt={category.name}
                className={styles.image}
                onError={(e) => {
                  console.error(`Failed to load image for ${category.name}`);
                  e.target.src = '/images/placeholder.png';
                  e.target.onerror = null;
                }}
              />
              <div className={styles.overlay}>
                <h3>{category.name}</h3>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
