import React, { useState } from 'react';
import SearchBar from './components/SearchBar';
import ClothingCard from './components/ClothingCard';
import styles from './App.module.css';

const clothingItems = [
  { id: 1, name: 'Coat', image: '/images/coat.jpg' },
  { id: 2, name: 'Warm puffer', image: '/images/puffer.jpg' },
  { id: 3, name: 'Bomber', image: '/images/bomber.jpg' },
  { id: 4, name: 'Biker jacket', image: '/images/biker.jpg' },
  { id: 5, name: 'Denim jacket', image: '/images/denim.jpg' },
  { id: 6, name: 'Blazer (suit)', image: '/images/blazer.jpg' },
  { id: 7, name: 'Printed/checked', image: '/images/printed.jpg' },
  { id: 8, name: 'Basic tees', image: '/images/basic-tee.jpg' },
  { id: 9, name: 'Baseball tee', image: '/images/baseball-tee.jpg' },
  { id: 10, name: 'Crew neck sweat', image: '/images/crew-neck.jpg' },
];

function App() {
  const [searchQuery, setSearchQuery] = useState('');

  const filteredItems = clothingItems.filter(item =>
    item.name.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <div className={styles.container}>
      <h1 className={styles.title}>Clothing Catalog</h1>
      <SearchBar
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
      />
      <div className={styles.grid}>
        {filteredItems.map(item => (
          <ClothingCard
            key={item.id}
            name={item.name}
            image={item.image}
          />
        ))}
      </div>
    </div>
  );
}

export default App;
