import React, { useState } from 'react';
import styles from './StyleEditor.module.css';

export default function StyleEditor() {
  const [selectedProfile, setSelectedProfile] = useState(0);
  const [selectedCategory, setSelectedCategory] = useState('Basic jumpers');

  const profiles = [
    '/assets/profiles/profile1.png',
    '/assets/profiles/profile2.png',
    '/assets/profiles/profile3.png',
    '/assets/profiles/profile4.png',
    '/assets/profiles/profile5.png',
    '/assets/profiles/profile6.png',
    '/assets/profiles/profile7.png'
  ];

  const categories = [
    'Basic jumpers',
    'Warm puffer',
    'Bomber',
    'Biker jacket'
  ];

  const toolbarItems = [
    { icon: '/assets/icons/edit.png', label: 'Edit' },
    { icon: '/assets/icons/style.png', label: 'Style' },
    { icon: '/assets/icons/grid.png', label: 'Grid' },
    { icon: '/assets/icons/size.png', label: 'Size' },
    { icon: '/assets/icons/wallet.png', label: 'Buy' },
    { icon: '/assets/icons/close.png', label: 'Close' }
  ];

  const bottomNavItems = [
    { icon: '/assets/icons/collar.png', label: 'Collar' },
    { icon: '/assets/icons/styles.png', label: 'Styles' },
    { icon: '/assets/icons/pant.png', label: 'Pant' },
    { icon: '/assets/icons/coat.png', label: 'Coat' },
    { icon: '/assets/icons/accessories.png', label: 'Accessories' },
    { icon: '/assets/icons/tools.png', label: 'Tools' }
  ];

  return (
    <div className={styles.container}>
      {/* Top Bar */}
      <div className={styles.topBar}>
        <button className={styles.backButton}>
          <img src="/assets/icons/back.png" alt="Back" />
        </button>
        <button className={styles.cartButton}>
          <img src="/assets/icons/cart.png" alt="Cart" />
        </button>
      </div>

      {/* Main Content */}
      <div className={styles.mainContent}>
        <img src="/assets/man.png" alt="Model" className={styles.mainImage} />
      </div>

      {/* Right Toolbar */}
      <div className={styles.rightToolbar}>
        <div className={styles.toolbarLabel}>Tools</div>
        {toolbarItems.map((item, index) => (
          <button key={index} className={styles.toolbarButton}>
            <img src={item.icon} alt={item.label} className={styles.toolbarIcon} />
          </button>
        ))}
      </div>

      {/* Bottom Section */}
      <div className={styles.bottomSection}>
        {/* Profile Carousel */}
        <div className={styles.profileCarousel}>
          {profiles.map((profile, index) => (
            <button
              key={index}
              className={`${styles.profileButton} ${selectedProfile === index ? styles.selected : ''}`}
              onClick={() => setSelectedProfile(index)}
            >
              <img src={profile} alt={`Profile ${index + 1}`} className={styles.profileImage} />
            </button>
          ))}
        </div>

        {/* Categories */}
        <div className={styles.categories}>
          {categories.map((category, index) => (
            <button
              key={index}
              className={`${styles.categoryButton} ${selectedCategory === category ? styles.selected : ''}`}
              onClick={() => setSelectedCategory(category)}
            >
              {category}
            </button>
          ))}
        </div>

        {/* Bottom Navigation */}
        <div className={styles.bottomNav}>
          {bottomNavItems.map((item, index) => (
            <button key={index} className={styles.navButton}>
              <img src={item.icon} alt={item.label} className={styles.navIcon} />
              <span className={styles.navLabel}>{item.label}</span>
            </button>
          ))}
        </div>
      </div>
    </div>
  );
}
