import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import styles from './StyleSelector.module.css';

const profileImages = [
  { id: 1, image: '/assets1/man.png', label: 'Style 1' },
  { id: 2, image: '/assets1/Frame 1597881673.png', label: 'Style 2' },
  { id: 3, image: '/assets1/Frame 1597881674.png', label: 'Style 3' },
  { id: 4, image: '/assets1/Frame 1597881674 (1).png', label: 'Style 4' },
  { id: 5, image: '/assets1/polo-style.png', label: 'Style 5' },
  { id: 6, image: '/assets1/man.png', label: 'Style 6' },
  { id: 7, image: '/assets1/man.png', label: 'Style 7' }
];

// Define the category options that appear above the bottom toolbar
const categories = [
  'Basic jumpers',
  'Warm puffer',
  'Bomber',
  'Biker jacket'
];

// Define the tools that appear in the bottom toolbar
const bottomTools = [
  { 
    icon: '/assets1/icons/collar.png', 
    label: 'Collar' 
  },
  { 
    icon: '/assets1/icons/styles.png', 
    label: 'Styles' 
  },
  { 
    icon: '/assets1/icons/pant.png', 
    label: 'Pant' 
  },
  { 
    icon: '/assets1/icons/coat.png', 
    label: 'Coat' 
  },
  { 
    icon: '/assets1/icons/accessories.png', 
    label: 'Accessories' 
  },
  { 
    icon: '/assets1/icons/tools.png', 
    label: 'Tools' 
  }
];

export default function StyleSelector() {
  // Initialize navigation function from React Router
  const navigate = useNavigate();

  // State management using React hooks
  const [selectedProfile, setSelectedProfile] = useState(0);  // Track selected profile image
  const [selectedCategory, setSelectedCategory] = useState(0);  // Track selected category
  const [isFavorite, setIsFavorite] = useState(false);  // Track favorite status

  // Handler for close button click
  const handleClose = () => {
    navigate(-1);  // Go back to previous page
  };

  // Handler for favorite button click
  const toggleFavorite = () => {
    setIsFavorite(!isFavorite);  // Toggle favorite status
  };

  // Handler for profile image selection
  const handleProfileClick = (index) => {
    setSelectedProfile(index);  // Update selected profile
  };

  return (
    <div className={styles.container}>
      {/* Status Bar */}
      <div className={styles.statusBar}>
        <span>12:30</span>
        <div className={styles.statusIcons}>
          <span>üì∂</span>
          <span>üîã</span>
        </div>
      </div>

      {/* Header */}
      <div className={styles.header}>
        <button className={styles.closeButton} onClick={handleClose}>‚úï</button>
        <button className={styles.cartButton}>
          <img 
            src="/assets1/icons/cart.png" 
            alt="Cart"
            className={styles.headerIcon}
          />
        </button>
      </div>

      {/* Star Icon */}
      <div className={styles.starContainer}>
        <img 
          src="/assets1/icons/star.png" 
          alt="Star" 
          className={styles.starIcon}
        />
      </div>

      {/* Heart Icon */}
      <div className={styles.heartContainer}>
        <button className={styles.heartButton} onClick={toggleFavorite}>
          <img 
            src="/assets1/icons/heart-circle.png" 
            alt="Favorite"
            className={`${styles.heartIcon} ${isFavorite ? styles.active : ''}`}
          />
        </button>
      </div>

      {/* Main Image */}
      <div className={styles.mainImageContainer}>
        <img 
          src={profileImages[selectedProfile].image}
          alt="Style preview"
          className={styles.mainImage}
          onError={(e) => {
            e.target.src = '/assets1/man.png'; // Fallback image
          }}
        />
      </div>

      {/* Camera Button */}
      <button className={styles.cameraButton}>
        <img 
          src="/assets1/icons/camera.png" 
          alt="Camera"
          className={styles.cameraIcon}
        />
      </button>

      {/* Profile Circles */}
      <div className={styles.profilesContainer}>
        {profileImages.map((profile, index) => (
          <button
            key={profile.id}
            className={`${styles.profileButton} ${selectedProfile === index ? styles.selectedProfile : ''}`}
            onClick={() => handleProfileClick(index)}
          >
            <img
              src={profile.image} 
              alt={`Profile ${index + 1}`}
              className={styles.profileImage}
              onError={(e) => {
                e.target.src = '/assets1/man.png'; // Fallback image
              }}
            />
          </button>
        ))}
      </div>

      {/* Categories */}
      <div className={styles.categoriesContainer}>
        {categories.map((category, index) => (
          <button
            key={index}
            className={`${styles.categoryButton} ${selectedCategory === index ? styles.selectedCategory : ''}`}
            onClick={() => setSelectedCategory(index)}
          >
            {category}
          </button>
        ))}
        <button className={styles.searchButton}>
          üîç
        </button>
      </div>

      {/* Bottom Toolbar */}
      <div className={styles.bottomToolbar}>
        {bottomTools.map((tool, index) => (
          <button key={index} className={styles.toolbarButton}>
            <img 
              src={tool.icon} 
              alt={tool.label}
              className={styles.toolbarIcon}
              onError={(e) => {
                e.target.src = '/assets1/icons/tools.png'; // Fallback icon
              }}
            />
            <span className={styles.toolbarLabel}>{tool.label}</span>
          </button>
        ))}
      </div>
    </div>
  );
}
