import React, { useState } from 'react';
import SearchBar from './components/SearchBar';
import ClothingCard from './components/ClothingCard';
import styles from './App.module.css';
import { images } from './assets/images/clothing';

const clothingItems = [
  { 
    id: 1, 
    name: 'Coat', 
    image: images.coat
  },
  { 
    id: 2, 
    name: 'Warm puffer', 
    image: images.warmPuffer
  },
  { 
    id: 3, 
    name: 'Bomber', 
    image: images.bomber
  },
  { 
    id: 4, 
    name: 'Biker jacket', 
    image: images.bikerJacket
  },
  { 
    id: 5, 
    name: 'Denim jacket', 
    image: images.denimJacket
  },
  { 
    id: 6, 
    name: 'Blazer (suit)', 
    image: images.blazer
  },
  { 
    id: 7, 
    name: 'Printed/checked', 
    image: images.printed
  },
  { 
    id: 8, 
    name: 'Basic tees', 
    image: images.basicTees
  },
  { 
    id: 9, 
    name: 'Baseball tee', 
    image: images.baseballTee
  },
  { 
    id: 10, 
    name: 'Crew neck sweat', 
    image: images.crewNeck
  }
];

function App() {
  const [searchQuery, setSearchQuery] = useState('');

  const filteredItems = clothingItems.filter(item =>
    item.name.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <div className={styles.container}>
      <h1 className={styles.title}>Clothing Catalog</h1>
      <SearchBar
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
      />
      <div className={styles.grid}>
        {filteredItems.map(item => (
          <ClothingCard
            key={item.id}
            name={item.name}
            image={item.image}
          />
        ))}
      </div>
    </div>
  );
}

export default App;
