import React, { useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import styles from './StyleDetails.module.css';

const styleVariations = [
  {
    id: 1,
    name: 'Classic Fit',
    image: '/assets1/Frame 1597881673.png'
  },
  {
    id: 2,
    name: 'Modern Fit',
    image: '/assets1/Frame 1597881674.png'
  },
  {
    id: 3,
    name: 'Slim Fit',
    image: '/assets1/polo-style.png'
  },
  {
    id: 4,
    name: 'Regular Fit',
    image: '/assets1/Frame 1597881674 (1).png'
  }
];

const categories = [
  'Basic jumpers',
  'Warm puffer',
  'Bomber',
  'Biker jacket'
];

const tools = [
  { icon: '👔', label: 'Collar', active: true },
  { icon: '👕', label: 'Styles' },
  { icon: '👖', label: 'Pant' },
  { icon: '🧥', label: 'Coat' },
  { icon: '👜', label: 'Accessories' },
  { icon: '🔧', label: 'Tools' }
];

export default function StyleDetails() {
  const navigate = useNavigate();
  const { id } = useParams();
  const [selectedVariation, setSelectedVariation] = useState(0);
  const [isFavorite, setIsFavorite] = useState(false);
  const [selectedCategory, setSelectedCategory] = useState(0);

  const handleClose = () => {
    navigate(-1);
  };

  const toggleFavorite = () => {
    setIsFavorite(!isFavorite);
  };

  return (
    <div className={styles.container}>
      {/* Header */}
      <div className={styles.header}>
        <button className={styles.closeButton} onClick={handleClose}>
          ✕
        </button>
        <div className={styles.brandLogo}>
          <span className={styles.sparkle}>✨</span>
          <span>Areta360</span>
        </div>
        <div className={styles.headerIcons}>
          <button className={styles.iconButton}>🛒</button>
          <button 
            className={`${styles.iconButton} ${isFavorite ? styles.active : ''}`}
            onClick={toggleFavorite}
          >
            {isFavorite ? '❤️' : '🤍'}
          </button>
        </div>
      </div>

      {/* Main Image Display */}
      <div className={styles.mainImageContainer}>
        <img 
          src={styleVariations[selectedVariation].image}
          alt={styleVariations[selectedVariation].name}
          className={styles.mainImage}
        />
      </div>

      {/* Style Variations */}
      <div className={styles.variationsContainer}>
        <div className={styles.variations}>
          {styleVariations.map((variation, index) => (
            <button
              key={variation.id}
              className={`${styles.variationButton} ${selectedVariation === index ? styles.selected : ''}`}
              onClick={() => setSelectedVariation(index)}
            >
              <img 
                src={variation.image} 
                alt={variation.name}
                className={styles.variationImage}
              />
            </button>
          ))}
        </div>
      </div>

      {/* Categories */}
      <div className={styles.categories}>
        {categories.map((category, index) => (
          <button
            key={index}
            className={`${styles.categoryButton} ${selectedCategory === index ? styles.selectedCategory : ''}`}
            onClick={() => setSelectedCategory(index)}
          >
            {category}
          </button>
        ))}
        <button className={styles.moreButton}>
          🔍
        </button>
      </div>

      {/* Bottom Toolbar */}
      <div className={styles.toolbar}>
        {tools.map((tool, index) => (
          <button 
            key={index}
            className={`${styles.toolButton} ${tool.active ? styles.activeToolButton : ''}`}
          >
            <span className={styles.toolIcon}>{tool.icon}</span>
            <span className={styles.toolLabel}>{tool.label}</span>
          </button>
        ))}
      </div>

      {/* AI Style Button */}
      <button className={styles.aiButton}>
        <span className={styles.aiIcon}>🤖</span>
        <span className={styles.aiLabel}>AI Style</span>
      </button>
    </div>
  );
}
